import React, { useState } from "react";

function Tile({ puzzle, grid, gridInfo, handleChange, handleClick }) {
  const [select, setSelect] = useState(false);

  function changeClass(rowIndex,colIndex,grid) {
    let className = "";
    gridInfo[rowIndex][colIndex] === 1
    ? className = "tile selected"
    : puzzle[rowIndex][colIndex] !== 0
    ? className = "fixed"
    : grid[rowIndex][colIndex] !== 0
    ? className = "tile taken"
    : className = "tile"
    return className;
  }

  return grid.map((row, rowIndex) => {
    return row.map((col, colIndex) => {
      return (
        <div key={rowIndex + " " + colIndex}>
          <div className={true ? "line" : "chota"} />
          <input
            className={
                changeClass(rowIndex,colIndex,grid)
            }
            value={col === 0 ? "" : col}
            type="text"
            id={rowIndex + " " + colIndex}
            onChange={(e) => handleChange(rowIndex, colIndex, e)}
            onClick={(e) => handleClick(rowIndex, colIndex, e)}
            autoComplete="off"
          />
        </div>
      );
    });
  });
}

export default Tile;
