import React, { useState } from "react";

function Tile({ puzzle, grid, gridInfo, handleChange, handleClick }) {
  const [select, setSelect] = useState(false);

  function changeClass(e) {
    //console.log(e);
    // document.getElementById("1").className = "tile selected";
  }
  console.log("after");

  return grid.map((row, rowIndex) => {
    return row.map((col, colIndex) => {
      console.log("454");
      return (
        <div key={rowIndex + " " + colIndex}>
          <div className={rowIndex % 3 == 0 ? "line" : "chota"} />
          <div className={rowIndex % 3 == 0 ? "vertical-line" : "chota"} />
          <input
            className={
              gridInfo[rowIndex][colIndex] === 1
                ? "tile selected"
                : puzzle[rowIndex][colIndex] !== 0
                ? "fixed"
                : col !== 0
                ? "tile taken"
                : "tile"
            }
            value={col === 0 ? "" : col}
            type="text"
            id={rowIndex + " " + colIndex}
            onChange={(e) => handleChange(rowIndex, colIndex, e)}
            onClick={(e) => handleClick(rowIndex, colIndex, e)}
            autoComplete="off"
          />
        </div>
      );
    });
  });
}

export default Tile;
