function Tile({ puzzle, grid, gridInfo, handleChange, handleClick }) {

  function changeClass(rowIndex, colIndex, grid) {
    let className = "tile";
    gridInfo[rowIndex][colIndex] === 1
      ? (className += " selected")
      : puzzle[rowIndex][colIndex] !== 0
      ? (className += " fixed")
      : grid[rowIndex][colIndex] !== 0
      ? (className += " taken")
      : (className = "tile")

    if(rowIndex%3===0) className += " horizontal-line-top"
    if(colIndex%3===0) className += " vertical-line-left"
    if(rowIndex === 8) className += " horizontal-line-bottom"
    if(colIndex === 8) className += " vertical-line-right"
    return className;
  }

  return grid.map((row, rowIndex) => {
    return row.map((col, colIndex) => {
      return (
        <div
          tabIndex="1"
          className={changeClass(rowIndex, colIndex, grid)}
          key={rowIndex + " " + colIndex}
          type="text"
          id={rowIndex + " " + colIndex}
          //onChange={(e) => handleChange(rowIndex, colIndex, e)}
          onClick={(e) => handleClick(rowIndex, colIndex, e)}
          onKeyDown={(e) => handleChange(rowIndex, colIndex, e)}
          autoComplete="off"
        >{col === 0 ? "" : col}</div>
      );
    });
  });
}

export default Tile;
